import React from 'react';
import Logo2 from '../assets/Logo2.jpeg';





const AboutUs = () => {
  return (
    <section className="bg-gray-50 py-16 px-6">
      <div className="max-w-screen-xl mx-auto">
        {/* Header Section */}
        <header className="text-center mb-16">
          <h1 className="text-6xl font-bold text-sky-500 mb-4">
            Sree Institute of Info Tech (SIIT)
          </h1>
          {/* Replace with your logo */}
          <div className="flex justify-center mb-6">
            <img
              src="/path/to/your/logo.png" // Correct the path here to your logo
              alt="SIIT Logo"
              className="w-40 h-auto object-contain" // Adjust the width/height as needed
            />
          </div>
          <p className="text-xl text-gray-600">
            Empowering the Next Generation of Developers
          </p>
        </header>

        {/* About Us Section */}
        <div className="lg:flex items-center justify-between gap-8 mb-16">
          {/* Image Section */}
        

          {/* Text Section */}
          <div className="lg:w-1/2 w-full text-center lg:text-left">
            <h3 className="text-3xl font-semibold text-gray-800 mb-4">Welcome to SIIT</h3>
            <p className="text-gray-600 mb-6">
              Sree Institute of Info Tech (SIIT) is a premier institute providing high-quality education in fields such as full-stack development, data structures, and algorithms. With over 20 years of experience in the field, we are dedicated to providing a hands-on, practical approach to learning.
            </p>
            <p className="text-gray-600 mb-6">
              We specialize in languages like C, C++, and advanced data structures to ensure our students are equipped with the necessary skills to succeed in the tech industry. Join us and shape your future with world-class education and training.
            </p>
          </div>
        </div>
        </div>
        </section>

       
  );
};

export default AboutUs;
