import React, { useState } from 'react';

// Import images from the 'assets' folder
import banner1 from '../assets/banner1.jpg';


const BannerCarousel = () => {
  const banners = [
    { src: banner1, alt: 'Banner 1' }
  
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  // Function to go to the next banner
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % banners.length);
  };

  // Function to go to the previous banner
  const prevSlide = () => {
    setCurrentIndex(
      (prevIndex) => (prevIndex - 1 + banners.length) % banners.length
    );
  };

  return (
    <div className="relative">
      {/* Carousel container */}
      <div className="carousel overflow-hidden relative">
        {/* Banner images */}
        <div
          className="carousel-inner flex transition-transform duration-500"
          style={{ transform: `translateX(-${currentIndex * 100}%)` }}
        >
          {banners.map((banner, index) => (
            <div key={index} className="carousel-item flex-shrink-0 w-full">
              <img
                src={banner.src}
                alt={banner.alt}
                className="w-full h-64 sm:h-80 md:h-96 object-cover"
              />
            </div>
          ))}
        </div>

        {/* Prev/Next buttons */}
        <button
          onClick={prevSlide}
          className="absolute left-0 top-1/2 transform -translate-y-1/2 text-white bg-blue-500 p-2 rounded-full"
        >
          &#8592;
        </button>
        <button
          onClick={nextSlide}
          className="absolute right-0 top-1/2 transform -translate-y-1/2 text-white bg-blue-500 p-2 rounded-full"
        >
          &#8594;
        </button>
      </div>

      {/* Optional: Dots */}
      <div className="absolute bottom-0 left-0 right-0 flex justify-center space-x-2 py-2">
        {banners.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrentIndex(index)}
            className={`h-2 w-2 rounded-full ${currentIndex === index ? 'bg-blue-500' : 'bg-gray-400'}`}
          ></button>
        ))}
      </div>
    </div>
  );
};

export default BannerCarousel;
